mapper.select:
select rnum, inquiry_id, inquiry_sabangid, inquiry_type, inquiry_ansstate, inquiry_title, inquiry_writer, inquiry_date, inquiry_explain, inquiry_anscontent
from (
	select rownum as rnum, inquiry_id, inquiry_sabangid, inquiry_type, inquiry_ansstate, inquiry_title, inquiry_writer, inquiry_date, inquiry_explain, inquiry_anscontent
	from (
		select inquiry_id, inquiry_sabangid, inquiry_type, inquiry_ansstate, inquiry_title, inquiry_writer, inquiry_date, inquiry_explain, inquiry_anscontent
		from inquiry
		where inquiry_sabangid=#{inquiry_sabangid}
		order by inquiry_id desc
	)
	where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}

mapper.update:
update inquiry set
inquiry_ansstate=#{inquiry_ansstate},
inquiry_anscontent=#{inquiry_anscontent}
where inquiry_id=#{inquiry_id}
