mapper.update:

   	  	update palbang set 
		palbang_likecount = palbang_likecount+1
		where palbang_id=#{palbang_id}


   	  	update palbang set 
		palbang_likecount = palbang_likecount-1
		where palbang_id=#{palbang_id}
